// import podium from "./podium.webp";
import podium from "./podium.png";
import "./App.css";
import Confetti from "react-confetti";
import { useEffect } from "react";
import { useState } from "react";
import { charactersWin } from "./assets/mario_kart/characters/charactersStates";

export const Podium = (props) => {
  const getWindowSize = () => {
    const { innerWidth, innerHeight } = window;
    return { innerWidth, innerHeight };
  };
  const nameKey1 = props.firstPlaceChar;
  const nameKey2 = props.secondPlaceChar;
  const nameKey3 = props.thirdPlaceChar;

  const handleClick = () => {
    window.location.reload(false);
  };

  useEffect(() => {
    setTimeout(() => {
      handleClick();
    }, 10000);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // console.log(nameKey1, nameKey2, nameKey3);
  // const firstThree =
  // console.log("image path", charactersWin);
  const [windowSize, setWindowSize] = useState(getWindowSize());
  useEffect(() => {
    function handleWindowResize() {
      setWindowSize(getWindowSize());
    }

    window.addEventListener("resize", handleWindowResize);

    return () => {
      window.removeEventListener("resize", handleWindowResize);
    };
  }, []);
  return (
    <div>
      <Confetti width={windowSize.innerWidth} height={windowSize.innerHeight} />
      <div className="img-container">
        <img className="podium-image" src={podium} alt={"podium"} />
        <div className="ranking">
          <div className="rank" id="second">
            <div className="logo-img-container">
              <img className="logo-img" src={props.logo2} alt={props.logo2} />
            </div>
            <div className="character-img-container" id="second-img-container">
              <img
                className="charPodium"
                src={charactersWin[nameKey2]}
                alt={nameKey2}
              />
            </div>
          </div>
          <div className="rank" id="first">
            <div className="logo-img-container">
              <img className="logo-img" src={props.logo1} alt={props.logo1} />
            </div>
            <div className="character-img-container" id="first-img-container">
              <img
                className="charPodium"
                src={charactersWin[nameKey1]}
                alt={nameKey1}
              />
            </div>
          </div>
          <div className="rank" id="third">
            <div className="logo-img-container">
              <img className="logo-img" src={props.logo3} alt={props.logo3} />
            </div>
            <div className="character-img-container" id="third-img-container">
              <img
                className="charPodium"
                src={charactersWin[nameKey3]}
                alt={nameKey3}
              />
            </div>
          </div>
        </div>
      </div>
      {/* </div> */}
      <div className="back-button">
        <button onClick={() => window.location.reload(false)}>
          Start Again
        </button>
      </div>
    </div>
  );
};
