import "./App.css";

export const Sleep = (delay) => {
    let start = new Date().getTime();
    while (new Date().getTime() < start + delay);
}

export const Countdown = (props) => {
    console.log("Countdown rendered:",props.text);
    return(
        <div className="countdown" id="countdown">
            {props.text}
        </div>
    )
}
