import logo from "./MarioKartLogo.png";
import "./App.css";
import { Body } from "./Body.js";
import images from "./assets/christmas/backgrounds/images.js";
import { getRandomInt } from "./randomInt";
import { useState } from "react";
import { TopPage } from "./TopPage";
import { MusicPlayer } from "./MusicPlayer";
import { Road } from "./Road";
// import {
//   characters,
//   charactersFront,
//   charactersRight,
//   charactersWin,
// } from "./assets/mario_kart/characters/charactersStates.js";
// import { Podium } from "./Podium";

function App() {
  const [isShown, setIsShown] = useState(false);
  const changePage = () => {
    setIsShown(!isShown);
  };
  return (
    <div className="App">
      <div className="background-container">
        <div
          style={{
            backgroundImage: `url(${
              images[getRandomInt(0, images.length - 1)]
            })`,
          }}
          className="background"
        />
      </div>
      <div className="content-container">
        <div className="header">
          <div className="logo-png">
            <MusicPlayer />
            <img src={logo} className="App-logo" alt="logo" />{" "}
          </div>
          <div className="start-button">
            {!isShown && <TopPage logo={logo} changePage={changePage} />}
          </div>
        </div>
        <div>
          <body>
            {isShown && <Body logo={logo} changePage={changePage} />}
            <Road />
          </body>
        </div>
      </div>
      <link
        href="https://fonts.googleapis.com/css2?family=Press+Start+2P&display=swap"
        rel="stylesheet"
      />
    </div>
  );
}

export default App;
